css:
@media print {
    body {
        -webkit-print-color-adjust: exact;
        background: #fff;
    }
    .alert-info {
        background-color: #cff4fc;
        border-color: #b6effb;
    }
    .alert-danger {
        background-color: #f8d7da;
        border-color: #f5c2c7;
    }
    .table {
        border-collapse: collapse;
        width: 100%;
    }
    .table-bordered th,
    .table-bordered td {
        border: 1px solid #dee2e6;
        padding: 0.75rem;
        color: #000;
    }
    .letter-head {
        margin-bottom: 10px;
    }
    h6, strong {
        color: #000;
    }
    .text-center {
        text-align: center;
    }
    .text-right {
        text-align: right;
    }
    hr {
        border: 0;
        border-top: 1px solid #dee2e6;
        margin: 8px 0;
        width: 100%;
    }
    .container-fluid, .row, .col-auto {
        width: 100%;
        margin: 0;
        padding: 0;
    }
}
